<?phpclass Anzeige {	/**	 * Erstellt f端r die Karosserie ($karosserien ist nur eine Karosserie) pro Karosserie-Wert	 * ( foreach ( $karosserie as $key => $value ) ) einen Listenpunkt	 *	 * @param $karosserien	 */	function detailView( $karosserien ) {		foreach ( $karosserien as $karosserie ) {			echo "<ul>";			foreach ( $karosserie as $key => $value ) {				echo "<li>".$key.": ".$value."</li>";			}			echo "</ul>";		}	}	/**	 * Gibt f端r jede Karosserie ( foreach ( $karosserien as $karosserie ) ) ein Formular zur端ck.	 * $inputArgs wird f端r jeden Einzelwert einer Karosserie erweitert (sieht dann aus wie in der index.php)	 *	 * @param $karosserien	 */	function overview( $karosserien ) {		echo "<br><br>";		foreach ( $karosserien as $karosserie ) {			$inputArgs = [];			foreach ( $karosserie as $key => $value ) {				$inputArgs[] = [					'label'       => $value,					'id'          => $key,					'type'        => 'hidden',					'placeholder' => '',					'value'       => $value,				];			}			$formular = new Formular();			$formular->form( 'post', 'details.php', $inputArgs, 'Details' );		}	}}