<?phpclass Dimensions extends Body {	var $karosserieID;	var $length;	var $width;	var $height;	var $trackFront;	var $trackRear;	var $trackMean;	var $wheelbase;	var $contactArea;	function setObjectValues() {		$this->objectValues = [			'length',			'width',			'height',			'trackFront',			'trackRear',			'trackMean',			'wheelbase',			'contactArea'		];	}	function getValues( $wantedValues, $restArray ) {		$db     = new Datenbank();		$dbh    = $db->connect();		$result = $db->select( $dbh );		$values = new FilterArray();		$values = $values->filter( $result, $restArray );		return [ 'values' => $values, 'wantedValues' => $wantedValues ];	}}