<?phpclass EuroCarSegment extends Body {	var $shortEuroCarSegment;	var $euroCarSegment;	function setObjectValues() {		$this->objectValues = [ 'euroCarSegment' ];	}	function getValues( $wantedValues, $restArray ) {		$db     = new Datenbank();		$dbh    = $db->connect();		$result = $db->select( $dbh );		$values = new FilterArray();		$values = $values->filter( $result, $restArray );		return [ 'values' => $values, 'wantedValues' => $wantedValues ];	}}