<?phpclass FilterArray {	/**	 * Filtert die Werte in $allowed aus dem $result-Array und gibt dieses zurÃ¼ck	 * http://stackoverflow.com/questions/4260086/php-how-to-use-array-filter-to-filter-array-keys	 *	 * @param $result, $allowed	 *	 * @return mixed	 */	function filter( $result, $allowed ) {		foreach ( $result as $key => $ar ) {			$searchArray           = $result[ $key ];			$finalResult           = array_intersect_key( $searchArray, array_flip( $allowed ) );			$tmp["karosserie$key"] = $finalResult;		}		return ( $tmp );	}}