<?phpclass Formular {	/**	 * Erzeugt ein form-Element mit der übergebenen Methode und dem action-Ziel.	 * Ruft die Methode zur Erzeugung der Eingabefelder auf	 *	 * @param $formMethod, $formAction, $inputArgs, $submitLabel	 */	function form( $formMethod, $formAction, $inputArgs, $submitLabel ) { ?>		<form method="<?php echo $formMethod; ?>" action="<?php echo $formAction; ?>">			<?php $this->input( $inputArgs ); ?>			<input type="submit" value="<?php echo $submitLabel; ?>">		</form>	<?php }	/**	 * Erzeugt für jedes „Unter-Array“ der $inputArgs ein Eingabefeld	 *	 * @param $inputArgs	 */	function input( $inputArgs ) {		foreach ( $inputArgs as $input ) {			echo "<div>";			if ( $input['label'] != '' ) { ?>				<label for="<?php echo $input['id'] ?>"><?php echo $input['label'] ?></label>			<?php } ?>			<input type="<?php echo $input['type'] ?>" id="<?php echo $input['id'] ?>" name="<?php echo $input['id'] ?>"			       placeholder="<?php echo $input['placeholder'] ?>" class="typeahead <?php if ( isset ( $input['class'] ) ) { echo $input['class']; } ?>"			       value="<?php if ( $input['value'] ) { echo $input['value']; } elseif ( $_POST ) { echo $_POST[$input['id']];}?>">			</div>		<?php		}	}}