<?phpclass Process extends Body {	function setObjectValues() {		$this->objectValues = [			'process',			'reTooling',			'degreeOfMechanisation',			'intendedProductionVolume',			'carBody',			'partsInTheBodyInWhite',			'totalNumberOfParts',			'numberOfPartsInWeightClassLessThan100g',			'numberOfPartsInWeightClass100gTo1kg',			'numberOfPartsInWeightClass1kgTo5kg',			'numberOfPartsInWeightClassMoreThan5kg'		];	}	function getValues( $wantedValues, $restArray ) {		$db     = new Datenbank();		$result = $db->select();		$values = new FilterArray();		$values = $values->filter( $result, $restArray );		return [ 'values' => $values, 'wantedValues' => $wantedValues ];	}}