<?phpclass Weights extends Body {	function setObjectValues() {		$this->objectValues = [			'frontDoors',			'rearDoors',			'hood',			'tailgate',			'frontFenders',			'hinges',			'fuelFlap',			'frontEndModules',			'totalWeight'		];	}	function getValues( $wantedValues, $restArray ) {		$db     = new Datenbank();		$dbh    = $db->connect();		$result = $db->select( $dbh );		$values = new FilterArray();		$values = $values->filter( $result, $restArray );		return [ 'values' => $values, 'wantedValues' => $wantedValues ];	}}