tual submit of the form.		};		$('input').on('input', function () {			getData();			getScript();		});		$('input').bind('typeahead:selected', function () {			getData();			getScript();		});		$(form).submit(function () {			var url = "get-result.php"; // the script where you handle the form input.			$.ajax({				type: "POST",				url: url,				data: $(form).serialize(), // serializes the form's elements.				success: function (data) {					$("#result").html(data); // show response from the php script.				}			});			return false; // avoid to execute the actual submit of the form.		});	})</script><script>	$( function() {		<?php		/**		* Hol den Typeahead-Code		*/		$karosserieJson   = new Karosserie();		$karosserieJson->setObjectValues();		$wantedValuesJson = [ 'oemBrand', 'carModel', 'modelYear' ];		$karosserienJson = $karosserieJson->getValues( $wantedValuesJson );		$displayJson     = new Anzeige();		$displayJson->typeahead( $wantedValuesJson );		?>	})</script><div id="script">	<script>		<?php		/**		 * JSON-Erzeugung beim Laden der Seite!		 */		$displayJson->json( $karosserienJson );		?>	</script></div></body></html>