<!DOCTYPE html><html><head>	<meta charset="UTF-8">	<link rel="stylesheet" href="css/style.css">	<script src="http://code.jquery.com/jquery-2.1.4.js"></script>	<script src="js/typehead.js"></script></head><body><?phprequire_once ( 'classes/Karosserie.class.php' );$inputArgs = [	[		'label' => 'Hersteller',		'id' => 'oembrand_oemBrand',		'class' => 'oemBrand',		'type' => 'text',		'placeholder' => 'Hersteller',		'value' => '',	],	[		'label' => 'Modell',		'id' => 'carmodel_carModel',		'class' => 'carModel',		'type' => 'text',		'placeholder' => 'Modell',		'value' => '',	],	[		'label' => 'Modelljahr',		'id' => 'karosserie_modelYear',		'class' => 'modelYear',		'type' => 'text',		'placeholder' => 'Modelljahr',		'value' => '',	],	[		'label' => '',		'id' => 'wantedValues',		'class' => '',		'type' => 'hidden',		'placeholder' => '',		'value' => 'oemBrand,carModel,modelYear',	]];$formular = new Formular();$formular->form( 'post', 'index.php', $inputArgs, 'Suchen' );if ( ( isset( $_POST ) )&& count( array_filter( $_POST ) ) > 1  ) {	$karosserie   = new Karosserie(); // legt ein neues Karosserie-Objekt an	$wantedValues = explode( ',', $_POST['wantedValues'] ); // das sind die Werte, die wir nachher ausgeben möchten	$karosserie->setObjectValues(); // Aufruf der setObjectValues-Methode. Setzt die Werte, auf die die Karosserie-Klasse „zugreifen“ kann	$karosserien = $karosserie->getValues( $wantedValues ); // holt sich die Werte	$display     = new Anzeige();	$display->overview( $karosserien ); // Anzeigen der zurückerhaltenen Karosserien}?><noscript>	<?php include( 'get-result.php' ); ?></noscript><div id="result"></div><script>	$( function() {		var form = $("form").first();		var getScript = function () {			$.ajax({				url: "ajax-json.php", //Relative or absolute path to response.php file				type: 'post',				data: $(form).serialize(),				success: function (data) {					$( '#script' ).html(data);				}			});		};		var getData = function () {			var url = "get-result.php"; // the script where you handle the form input.			$.ajax({				type: "POST",				url: url,				data: $(form).serialize(), // serializes the form's elements.				success: function (data) {					$("#result").html(data); // show response from the php script.				}			});			return false; // avoid to execute the actual submit of the form.		};		$('input').on('input', function () {			getData();			getScript();		});		$('input').bind('typeahead:selected', function () {			getData();			getScript();		});		$(form).submit(function () {			var url = "get-result.php"; // the script where you handle the form input.			$.ajax({				type: "POST",				url: url,				data: $(form).serialize(), // serializes the form's elements.				success: function (data) {					$("#result").html(data); // show response from the php script.				}			});			return false; // avoid to execute the actual submit of the form.		});	})</script><script>	$( function() {		<?php		/**		* Hol den Typeahead-Code		*/		$karosserieJson   = new Karosserie();		$karosserieJson->setObjectValues();		$wantedValuesJson = [ 'oemBrand', 'carModel', 'modelYear' ];		$karosserienJson = $karosserieJson->getValues( $wantedValuesJson );		$displayJson     = new Anzeige();		$displayJson->typeahead( $wantedValuesJson );		?>	})</script><div id="script">	<script>		<?php		/**		 * JSON-Erzeugung beim Laden der Seite!		 */		$displayJson->json( $karosserienJson );		?>	</script></div></body></html>