input').on('input', function () {			getData();		});		var getScript = function () {			$.ajax({				url: "ajax-json.php", //Relative or absolute path to response.php file				type: 'post',				data: $(form).serialize(),				success: function (data) {					$('script').first().replaceWith(data);				}			});		};		getScript();		$('input').on('input', function () {			getScript();		});	});</script><?php	$karosserieJson   = new Karosserie(); // legt ein neues Karosserie-Objekt an	$wantedValuesJson = [ 'oemBrand', 'carModel', 'modelYear' ]; // das sind die Werte, die wir nachher ausgeben möchten	$karosserieJson->setObjectValues(); // Aufruf der setObjectValues-Methode. Setzt die Werte, auf die die Karosserie-Klasse „zugreifen“ kann	$karosserienJson = $karosserieJson->getValues( $wantedValuesJson ); // holt sich die Werte	$displayJson     = new Anzeige();	$displayJson->json( $karosserienJson ); ?></body></html>